package com.d4dl.hellofib.greeting;

import org.springframework.data.repository.CrudRepository;
import org.springframework.data.repository.query.Param;
import org.springframework.data.rest.core.annotation.RepositoryRestResource;

import java.util.List;

/**
 * Repository that implements an interface which will cause Spring Boot
 * to generate REST resources and publish them as endpoints.
 * @see Greeting for its attributes
 *
 * All other HTTP verb endpoints are generated by Spring.
 */
@RepositoryRestResource
public interface GreetingRepository extends CrudRepository<Greeting, Long> {
    /**
     * Finder method that will be used by Spring to generate a query that looks up greeters by name.
     * @param greeterName the name matched against the database column in the Greeting table
     * @return a list of all the greetings that greeter with the specified name was greeting worlds in
     */
    List<Greeting> findByGreeterName(@Param("greeterName") String greeterName);
}
