Jekyll::Hooks.register :site, :after_reset do |site|
  pageklass = Class.new(Jekyll::Page) do
    def initialize(site, base)
      @site = site
      @base = base
      @data = {}
      @dir = '/'
      @name = 'foo.html'
      @content = 'mytinypage'

      self.process(@name)
    end
  end

  site.pages << pageklass.new(site, site.source)
end
